import React, { useContext, useEffect } from 'react'
import styled from 'styled-components'
import { VscAccount, VscDeviceMobile, VscMail,VscEdit, VscTrash } from "react-icons/vsc"
import Dialog from '@material-ui/core/Dialog'
import DialogActions from '@material-ui/core/DialogActions'
import DialogContent from '@material-ui/core/DialogContent'
import DialogContentText from '@material-ui/core/DialogContentText'
import DialogTitle from '@material-ui/core/DialogTitle'

import ContactContext from '../context/contact/contactContext'

const ContainerList = styled.div`
  display: flex;
  justify-content: space-around;
  margin-top: 5rem;
`

const CardContact = styled.div`
  border-radius: 3px;
  border: 1px solid ${props => props.theme.secundary};
  background-color: ${props => props.theme.lightgrey};
  margin-bottom: 1rem;
  padding: 1rem 2rem;

  svg {
    margin-right: 1rem;
  }
  button {
    cursor: pointer;
    padding: 0.4rem;
  }
  button:first-of-type {
    margin-right: 1rem;
    color: ${props => props.theme.offWhite};
    background-color: ${props => props.theme.secundary};
    border-radius: 3px;
    border-color: ${props => props.theme.secundary};
    &:hover {
      color: ${props => props.theme.secundary};
      background-color: ${props => props.theme.offWhite};
      border-radius: 3px;
      border-color: ${props => props.theme.secundary};
    }
  }

  button:last-of-type {
    color: ${props => props.theme.offWhite};
    background-color: ${props => props.theme.danger};
    border-radius: 3px;
    border-color: ${props => props.theme.danger};
    &:hover {
      color: ${props => props.theme.danger};
      background-color: ${props => props.theme.offWhite};
      border-radius: 3px;
      border-color: ${props => props.theme.danger};
    }
  }
`

const ContactLists = ({ list }) => {
  const contactContext = useContext(ContactContext)
  const { setCurrent, deleteContact } = contactContext

  const [contactToDelete, setContactToDelete] = useState({})
  const [openDelete, setOpenDelete] = useState(false)

  console.log(list)
  // useEffect(() => {
    // getLists()
    // return () => {
    //   cleanup
    // }
  // }, [])
  // console.log('lists ', lists)

  const handleOpenDelete = list => {
    setOpenDelete(true)
    setListToDelete(list)
  }

  const handleCloseDelete = () => {
    setOpenDelete(false)
  }


  const handleEdit = contact => {
    console.log('handle edit ', contact)
  }

  const handleDelete = contact => {
    console.log('handle delete ', contact)
  }

  return (
    <div>
      <h3>{ list.title }</h3>
      {
        list && list.contacts.map(contact => (
          <CardContact key={contact.id}>
            <>
              <p><VscAccount />{contact.name}</p>
              <p><VscDeviceMobile />{contact.phone}</p>
              <p><VscMail />{contact.email}</p>
              <p>
                <button>
                  <VscEdit onClick={() => handleEdit(contact)}/>Editar
                </button>
                <button>
                  <VscTrash onClick={() => handleOpenDelete(contact)} />Remover
                </button>
              </p>
            </>
          </CardContact>
        ))
      }
      <Dialog
        open={openDelete}
        onClose={handleCloseDelete}
        aria-labelledby="responsive-dialog-title"
      >
        <DialogTitle id="responsive-dialog-title">{`Deletar contato - ${listToDelete.title}?`}</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Atenção, ao deletar a lista, todos os contatos da lista também serão deletados.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button autoFocus onClick={handleCloseDelete} color="primary">
            Cancelar
          </Button>
          <Button onClick={handleDelete} color="primary" autoFocus>
            Deletar
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  )
}

export default ContactLists
