import styled from 'styled-components'

const Container = styled.div`
  margin: 0;
  padding: 0;

  @media (max-width: 1300px) {
    border-top: 1px solid ${props => props.theme.lightgrey};
    width: 100%;
    justify-content: center;
    font-size: 1.5rem;
  }
`

export default Container
