import axios from 'axios'

// require('dotenv').config()

const {
  REACT_APP_PROJECT_ENDPOINT,
  REACT_APP_PROJECT_API_HOST,
  REACT_APP_API_ENV
} = process.env

const backendHosts = {
  'development': 'http://localhost:3000',
  'production': REACT_APP_PROJECT_ENDPOINT
}

const currentHost = REACT_APP_PROJECT_API_HOST || backendHosts[REACT_APP_API_ENV]

const api = axios.create({
  baseURL: `${currentHost}`
})

export default api
