import React, { useContext, useEffect } from 'react'
import styled from 'styled-components'
import { VscTrash, VscEdit } from 'react-icons/vsc'

import ListContext from '../context/list/listContext'
import ContactLists from './ContactLists'

const ContainerList = styled.div`
  ul {
    li {
      list-style: none;
      border: 1px solid ${props => props.theme.lightgrey};
      padding: 1.5rem;
      border-radius: 3px;
      display: flex;
      justify-content: space-between;
      margin-top: 1rem;
      p:first-of-type {
        cursor: pointer;
      }
      svg {
        cursor: pointer;
        &:first-of-type {
          fill: ${props => props.theme.grey};
        }
        &:last-of-type {
          fill: ${props => props.theme.danger};
        }
      }
    }
  }
  display: grid;
  font-size: 16px;
  margin-top: 5rem;
`

const Lists = () => {
  const listContext = useContext(ListContext)
  const { lists, getLists, setCurrent } = listContext

  useEffect(() => {
    getLists()
  }, [])

  const handleClickList = e => {
    console.log('clicou na lista')
  }

  console.log('lists ', lists)
  return (
    <ContainerList>
      <ul>
      { lists && lists.map(list => (
          <>
            <li key={list.id} className='list-title'>
              <p onClick={handleClickList}>{list.title}</p>
              <p><VscEdit style={{ marginRight: '1rem' }} /><VscTrash /></p>
            </li>
            {/* <ContactLists items={list.contacts} /> */}
          </>
        ))}
      </ul>
    </ContainerList>
  )
}

export default Lists
