import React, { useContext } from 'react'
import Route from 'next/route'

import AuthContext from '../context/auth/authContext'

const login = '/login?redirected=true'

const checkUserAuthentication = () => {
  const authContext = useContext(AuthContext)
  const { isAuthenticated, loading } = authContext
  return { auth: isAuthenticated }

}

export default WrappedCompomemt => {
  hocComponent.getInitialProps = async (context) => {
    const userAuth = await checkUserAuthentication()

    if (!userAuth?.auth) {
      if (context.res) {
        context.res?.writeHead(302, {
          Location: login,
        })
        context.res?.end()
      } else {
        Route.replace(login)
      }
    } else if (WrappedCompomemt.getInitialProps) {
      const wrapperProps = await WrappedCompomemt.getInitialProps({ ...context, auth: userAuth })
      return { ...wrapperProps, userAuth }
    }
    return { userAuth }
  }
  return hocComponent
}
