import React, { useState, useContext, useEffect } from 'react'
import Router from 'next/router'
import Link from 'next/link'

import AlertContext from '../context/alert/alertContext'
import ListContext from '../context/list/listContext'
import Form from './styles/Form'

const NewList = () => {
  const alertContext = useContext(AlertContext)
  const listContext = useContext(ListContext)

  const { setAlert } = alertContext
  const { lists, addList, setCurrent } = listContext

  const [list, setList] = useState({
    title: ''
  })

  const { title } = list

  const onChange = event => setList({ ...user, [event.target.name]: event.target.value })

  const onSubmit = event => {
    event.preventDefault()
    if (title === '') {
      setAlert('Preencha o nome da lista', 'danger')
    } else {
      setList({
        ...list
      })
    }
    console.log('submit')
  }

  return (
    <>
      <Form
        onSubmit={onSubmit}      
      >
        <fieldset>
          <h3>Crie uma nova lista</h3>
          <label htmlFor="title">
            Nome
            <input
              type="text"
              name="title"
              placeholder="Digite o nome da lista"
              value={title}
              onChange={onChange}
            />
          </label>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            marginTop: '2.5rem'
          }}>
            <Link href='/login'>
              <a>Adicione contato em vez disso</a>
            </Link>
            <button type="submit">Criar</button>
          </div>
        </fieldset>
      </Form>
    </>
  )
}

export default NewList
